package VariableStudy;

public class NumericDecimal {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		
		float weight;
		weight=60.1234567890f;  //it takes 6 digits after decimal point
		System.out.println("my weight is "+weight+"kg ");
		
		double height;
		height=123.1234567890185432; //it takes 14 digits after decimal point
		System.out.println("my height is "+height+" cm");
				

	}

}
