package VariableStudy;

public class NumericNonDecimal {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		
		byte age;
		age=120;
		System.out.println("my friends age is "+age);
		
		short price;
		price=1236;
		System.out.println("the price of shirt is "+price);
		
		int ammount;
		ammount=1234567890;
		System.out.println("my ammount in rs is "+ammount);
		
		long population;
		population=1234567890123456781l; //at last write l character
		System.out.println("the worlds population is "+population);

	}

}
