package VariableStudy;

public class overallstudy {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
	
		//1. variable declaration
		
		String name;
		String remark;
		char gender;
        char grade;
        int rollno;
        int age;
        float  weight;
        float height;
         
        //2. variable initializatiaon or assign
        
        name="akshay chavan";
        remark="excellent";
        gender='M';
        grade='A';
        rollno=21;
        age=27;
        weight=61.50f;
        height=133.22f;
        
        //3.variable usage (to print)
        
        System.out.println("my name is "+name);
        System.out.println("my remark is "+remark);
        System.out.println("my gender is "+gender);
        System.out.println("my grade is "+grade);
        System.out.println("my rollno is "+rollno);
        System.out.println("my age is "+age+" years");
        System.out.println("my weight is "+weight+"kg");
        System.out.println("my height is "+height+"cm");

	}

}
